import React, {useState, useEffect} from 'react';
import { StyleSheet, TouchableOpacity, Text, View, Image, ImageBackground, ScrollView } from 'react-native';
import {
    widthPercentageToDP as wp,
    heightPercentageToDP as hp,
  } from 'react-native-responsive-screen';
import {Appbar} from 'react-native-paper'
import Icon2 from 'react-native-vector-icons/AntDesign';
import { DataTable } from 'react-native-paper';
import {connect} from 'react-redux';

const Account = (props)=>{
    
    return(
        <ImageBackground 
            style={ styles.imgBackground } 
            resizeMode='cover' 
            source={require('../../assets/bg3.jpg')}
        >
        <View>
            <ScrollView style={styles.ScrollView}>
                <Appbar.Header style={styles.headerContainer}>
                <View style={styles.container}>
                    <Image
                        style={styles.logo}
                        source={require('../../assets/img/logo.png')}
                    />
                    <Text style={styles.title}
                    >
                        Bienvenue dans votre Drive
                    </Text>            
                    <View>
                        <TouchableOpacity
                            onPress={()=>{
                                props.navigation.navigate('Logout')
                            }}
                        >
                            <Icon2 
                                size={30} 
                                name={'logout'} 
                                style={styles.icon}
                            />
                            <Text style={styles.logout}
                            >
                                Logout
                            </Text>
                        </TouchableOpacity>
                    </View>
                </View>
            </Appbar.Header>
                <View style={styles.boxHeader}>
                    <Text style={styles.textHeader}>
                        Vos informations de compte :
                    </Text>
                </View>
                <View style={styles.containerBox}>
                    <View style={styles.box}>
                        <View style={styles.column}>
                            <TouchableOpacity
                                onPress={()=>{
                                    props.navigation.navigate('AllOrders')     
                                }}
                            >
                            <Text style={styles.links}>Historique de toutes vos commandes</Text>
                            </TouchableOpacity>
                        </View>
                    </View>
                    <View style={styles.box}>
                        <View style={styles.column}>
                            <TouchableOpacity
                                onPress={()=>{
                                    props.navigation.navigate('AccountInformations', {id: props.user.infos.id})         
                                }}
                            >
                            <Text style={styles.links}>Vos informations personnelles</Text>
                            </TouchableOpacity>
                        </View>
                    </View>
                    <View style={styles.box}>
                        <View style={styles.column}>
                            <TouchableOpacity
                                onPress={()=>{
                                    props.navigation.navigate('CommunicationsPreferences')         
                                }}
                            >
                            <Text style={styles.links}>Vos préférences de communications</Text>
                            </TouchableOpacity>
                        </View>
                    </View>
                    <View style={styles.box2}>
                        <Text style={styles.h1}>Votre dernière commande</Text>
                        <DataTable>
                            <DataTable.Header>
                                <DataTable.Title style={{flex: 4}}>N° commande</DataTable.Title>
                                <DataTable.Title style={{flex: 4}}>Date</DataTable.Title>
                                <DataTable.Title style={{flex: 2}}>Montant</DataTable.Title>
                                <DataTable.Title style={{flex: 2}}>Status</DataTable.Title>
                            </DataTable.Header>
                            <DataTable.Row>
                                <DataTable.Cell style={{flex: 4}}>1284</DataTable.Cell>
                                <DataTable.Cell style={{flex: 4}}>01-03-2021</DataTable.Cell>
                                <DataTable.Cell style={{flex: 2}}>48€</DataTable.Cell>
                                <DataTable.Cell style={{flex: 2}}>Livré</DataTable.Cell>
                            </DataTable.Row>
                        </DataTable>
                    </View>
                </View>
            </ScrollView>
        </View>
        </ImageBackground>
    )
}

const styles = StyleSheet.create({

    imgBackground: {
        width: '100%',
        height: '100%',
        flex: 1 
    },
    ScrollView: {
        width: wp('100%'),
    },
    headerContainer: {
		backgroundColor: '#17202a',
        height:50
	},
	container: {
		flex: 1,
		alignItems: 'flex-end',
        flexDirection: 'row',
        justifyContent: 'space-between'
	},
    logo: {
        width:50,
        height:53,
    },
    title: {
        color:'#a3c2fa',
        fontSize: 18
    },
    h1: {
        fontSize: 25,
        textAlign: 'center',
        paddingTop: hp('1%'),
        paddingBottom: hp('1%')
    },
	icon: {
        color:'#a3c2fa',
    },
    logout: {
        color:"#a3c2fa",
    },
    boxHeader: {
        borderBottomWidth: 3,
        borderColor: '#fff',
        paddingTop: hp('3%'),
        paddingBottom: hp('3%')
    },
    textHeader: {
        color: "#17202a",
        fontWeight: "bold",
        textAlign: 'center',
        fontSize: 25
    },
    containerBox: {
        flexDirection: 'column',
        flex:1
    },
    box: {
        flexDirection: 'row',
        borderColor: '#fff',
        borderBottomWidth: 3,
        justifyContent: 'center',
        backgroundColor: '#17202a',
    },
    box2: {
        flexDirection: 'column'
    },
    column: {
        paddingTop: 10,
        paddingBottom: 10,
        alignItems: 'center',
        borderColor: 'gray',
        textAlign: 'center',
    },
    links: {
        color: '#a3c2fa'
    }

})
 
mapStateToProps = (store)=>{
    return {
        user: store.user
    }
}
export default connect(mapStateToProps)(Account   );
