import React , {useState} from 'react';
import { StyleSheet, Text, View, ImageBackground, TextInput, TouchableOpacity, ScrollView } from 'react-native';
import {
    widthPercentageToDP as wp
  } from 'react-native-responsive-screen';
import {registerUser} from '../../api/user';
import RNPickerSelect from "react-native-picker-select";

const Register = (props)=> {

	const [civility, setCivility] = useState(''); 
	const [firstName, setFirstName] = useState(''); 
	const [lastName, setLastName] = useState(''); 
	const [birthday, setBirthday] = useState('');
	const [address, setAddress] = useState('');
	const [zip, setZip] = useState('');
	const [city, setCity] = useState('');
	const [phone, setPhone] = useState('');
	const [email, setEmail] = useState('');
	const [password, setPassword] = useState('');

	const onSubmitForm = ()=>{
		let data = {
			civility: civility,
			firstName: firstName,
			lastName: lastName,
			birthday: birthday,
			zip: zip,
			city: city,
			phone: phone,
			email: email,
			password: password,
			address: address			
		}
		console.log("DATA",data)

		registerUser(data)
		.then((res)=>{
			console.log("AXIOS SAVE USER",res);
			if(res.status === 200) {
				props.navigation.navigate('Login')
			}
		})
	}

    return (
		<ImageBackground 
			style={ styles.imgBackground } 
			resizeMode='cover' 
			source={require('../../assets/bg3.jpg')}
		>
    	<View style={styles.container}>
    		<ScrollView style={styles.scrollContainer}>
    			<Text
					style={styles.title}
				>
					Enregistrez-vous :
				</Text>
				<View style={styles.rngPicker}>
					<RNPickerSelect 
						placeholder={{
							label: 'Civilit√© :', value: 'null'
						}}
						onValueChange={(value) => setCivility(value)}
						items={[
							{ label: "Monsieur", value: "Monsieur" },
							{ label: "Madame", value: "Madame" }
						]}	
	             	/> 
				</View>
    			<TextInput 
					style={styles.input}
					placeholder="Pr√©nom"
					placeholderTextColor="white"
					value={firstName}
                	onChangeText={(inputText)=>{
                    	setFirstName(inputText)
                	}}
            	/>
				<TextInput 
					style={styles.input}
					placeholder="Nom"
					placeholderTextColor="white" 
					value={lastName}
                	onChangeText={(inputText)=>{
                    	setLastName(inputText)
                	}}
            	/>
				<TextInput
    				style={styles.input}
					placeholder="Date de naissance"
					placeholderTextColor="white" 
					value={birthday}
                	onChangeText={(inputText)=>{
                    	setBirthday(inputText)
                	}}
    			/>
				<TextInput
    				style={styles.input}
					placeholder="Adresse"
					placeholderTextColor="white" 
					value={address}
                	onChangeText={(inputText)=>{
                    	setAddress(inputText)
                	}}
    			/>
				<TextInput
    				style={styles.input}
					placeholder="Code postal"
					placeholderTextColor="white" 
					value={zip}
                	onChangeText={(inputText)=>{
                    	setZip(inputText)
                	}}
    			/>
				<TextInput
    				style={styles.input}
					placeholder="Ville"
					placeholderTextColor="white" 
					value={city}
                	onChangeText={(inputText)=>{
                    	setCity(inputText)
                	}}
    			/>
				<TextInput
    				style={styles.input}
					placeholder="T√©l√©phone"
					placeholderTextColor="white" 
					value={phone}
                	onChangeText={(inputText)=>{
                    	setPhone(inputText)
                	}}
    			/>
				<TextInput
    				style={styles.input}
					placeholder="Email"
					placeholderTextColor="white"
					value={email}
                	onChangeText={(inputText)=>{
                    	setEmail(inputText)
                	}}
    			/>
				<TextInput
    				style={styles.input}
					placeholder="Mot de passe"
					placeholderTextColor="white" 
					secureTextEntry={true}
					value={password}
                	onChangeText={(inputText)=>{
                    	setPassword(inputText)
                	}}
    			/>
    			<TouchableOpacity
                onPress={(e)=>{
                    onSubmitForm()
                }}
                style={styles.button}
                >
                <Text style={styles.buttonText}>Cr√©er le compte</Text>
            </TouchableOpacity>
    		</ScrollView>
    	</View>
		</ImageBackground>
    )

}

const styles = StyleSheet.create({
  container: {
    flex: 1,
	paddingTop: 50
  },
  imgBackground: {
	width: '100%',
	height: '100%',
	flex: 1 
  },
  title: {
	fontSize: 20,
	textAlign: 'center',
	marginBottom: 20,
	color: "#17202a"
  },
  scrollContainer: {
	width: wp('100%'),
	textAlign: 'center',
  },
  rngPicker: {
	backgroundColor: '#17202a',
  	width: wp('60%'),
	height: 40,
	borderRadius: 50,
	marginBottom: 15,
	marginLeft: wp('20%'),
	paddingLeft: wp('5%'),
	color: "white"
  },
  input: {
	backgroundColor: '#17202a',
	width: wp('60%'),
	height: 40,
	borderRadius: 50,
	marginBottom: 15,
	marginLeft: wp('20%'),
	paddingLeft: wp('5%'),
	color: 'white'
  },
  button: {
	backgroundColor: "#219653",
	borderRadius: 50,
	width: wp('45%'),
	height: 40,
	alignItems: "center",
	justifyContent: "center",
	marginTop: wp('5%'),
	marginBottom: wp('10%'),
	marginLeft: wp('27%')
  },
  buttonText: {
	fontSize: 18,
	color: "white"
  }
});

export default Register;
