import React, { useState, useEffect } from 'react';
import { StyleSheet, TouchableOpacity, ImageBackground, Image, Text, View, ScrollView } from 'react-native';
import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,
} from 'react-native-responsive-screen';
import {Appbar} from 'react-native-paper'
import Icon4 from 'react-native-vector-icons/FontAwesome';
import {connect} from 'react-redux';

const Home = (props)=>{


const goToSection = (id)=> {
  props.navigation.navigate('ProductSection', {id: id})
}
  return (

    <ImageBackground 
      style={ styles.imgBackground } 
      resizeMode='cover' 
      source={require('../assets/bg3.jpg')}
    >
    <View> 
      <ScrollView style={styles.ScrollView}>
        <Appbar.Header style={styles.headerContainer}>
          <View style={styles.container}>
            <Image
              style={styles.logo}
              source={require('../assets/img/logo.png')}
            />
            <Text style={styles.title}
            >
              Bienvenue dans votre Drive
            </Text>            
            <View>
              <TouchableOpacity
                onPress={()=>{
                  props.navigation.navigate('Basket')
                }}
              >
                <Icon4 
                  size={30} 
                  name={'shopping-basket'} 
                  style={styles.icon}
                />
                <Text style={styles.textFromHeader}>
                  Panier
                </Text>
              </TouchableOpacity>
            </View>
          </View>
        </Appbar.Header>
        <View style={styles.boxHeader}>
          <View style={styles.innerHeader}>
            <TouchableOpacity
              onPress={()=>{
                props.navigation.navigate('Notes')
              }}
            >
              <Image
                style={styles.imgHeader}
                source={require('../assets/img/memo.png')}
              />
              <Text style={styles.text}>
                Notes
              </Text>
            </TouchableOpacity>
          </View>

          <View style={styles.innerHeader}>
            <TouchableOpacity
              onPress={()=>{
                props.navigation.navigate('Promotions')
              }}
            >  
            <Image
              style={styles.imgHeader}
              source={require('../assets/img/promo.png')}
            />
              <Text style={styles.text}>
                Promotions
              </Text>
            </TouchableOpacity>
          </View>

          <View style={styles.innerHeader}>
            <TouchableOpacity
              onPress={()=>{
                props.navigation.navigate('DriveMap')
              }}
            >
              <Image
                  style={styles.imgHeader}
                  source={require('../assets/img/map.png')}
              />
              <Text style={styles.text}>
                Magasins
              </Text>
            </TouchableOpacity>  
          </View>

        </View>
        <View style={styles.box}>
          <View style={styles.inner}>
            <TouchableOpacity
              onPress={()=>{
                goToSection(1)
              }}
            >
              <Image
                style={styles.img}
                source={require('../assets/img/section/bio.png')}
              />
              <Text style={styles.innerText}>
                Bio
