import React, {useState, useEffect} from 'react';
import { StyleSheet, TouchableOpacity, Text, View, Image, ImageBackground, ScrollView } from 'react-native';
import {
    widthPercentageToDP as wp,
    heightPercentageToDP as hp,
  } from 'react-native-responsive-screen';
import {Appbar} from 'react-native-paper'
import Icon2 from 'react-native-vector-icons/AntDesign';

const CommunicationsPreferences = (props)=>{
    
    return(
        <ImageBackground 
            style={ styles.imgBackground } 
            resizeMode='cover' 
            source={require('../../assets/bg3.jpg')}
        >
        <View>
            <ScrollView style={styles.ScrollView}>
                <Appbar.Header style={styles.headerContainer}>
                <View style={styles.container}>
                    <Image
                        style={styles.logo}
                        source={require('../../assets/img/logo.png')}
                    />
                    <Text style={styles.title}
                    >
                        Bienvenue dans votre Drive
                    </Text>            
                    <View>
                        <TouchableOpacity
                            onPress={()=>{
                                props.navigation.navigate('Logout')
                            }}
                        >
                            <Icon2 
                                size={30} 
                                name={'logout'} 
                                style={styles.icon}
                            />
                            <Text style={styles.logout}
                            >
                                Logout
                            </Text>
                        </TouchableOpacity>
                    </View>
                </View>
            </Appbar.Header>
                <View style={styles.boxHeader}>
                    <Text style={styles.textHeader}>
                        Vos informations de compte :
                    </Text>
                </View>
                <View style={styles.box}>
                    <View style={styles.column}>
                        <TouchableOpacity
                            onPress={()=>{
                                props.navigation.navigate('Account')         
                            }}
                        >
                        <Text>Votre dernière commande</Text>
                        </TouchableOpacity>
                    </View>
                    <View style={styles.column}>
                        <TouchableOpacity
                            onPress={()=>{
                                props.navigation.navigate('AllOrders')     
                            }}
                        >
                        <Text>Historique de toutes vos commandes</Text>
                        </TouchableOpacity>
                    </View>
                    <View style={styles.column}>
                        <TouchableOpacity
                            onPress={()=>{
                                props.navigation.navigate('AccountInformations')         
                            }}
                        >
                        <Text>Vos informations personnelles</Text>
                        </TouchableOpacity>
                    </View>
                </View>
            </ScrollView>
        </View>
        </ImageBackground>
    )
}

const styles = StyleSheet.create({
    
    ScrollView: {
        width: wp('100%'),
    },
    imgBackground: {
        width: '100%',
        height: '100%',
        flex: 1 
    },
    headerContainer: {
		backgroundColor: '#17202a',
        height:50
	},
	container: {
		flex: 1,
		alignItems: 'flex-end',
        flexDirection: 'row',
        justifyContent: 'space-between'
	},
    logo: {
        width:50,
        height:53,
    },
    title: {
        color:'#a3c2fa',
        fontSize: 18
    },
	icon: {
        color:'#a3c2fa',
    },
    logout: {
        color:"#a3c2fa",
    },
    boxHeader: {
        flex:1,
        flexDirection: 'row',
        justifyContent:'space-around',
        borderColor: 'gray',
        paddingTop: hp('1%'),
        borderBottomWidth: 3,
    },
    textHeader: {
        color: "#17202a",
        fontWeight: "bold",
        textAlign: 'center',
        fontSize: 25
    },
    box: {
        flex:1,
        flexDirection: 'column',
        borderTopWidth: 1,
        borderColor: 'gray',
        borderWidth: 3,
        width: '35%',
        paddingLeft: hp('1%'),
        paddingRight: hp('1%'),  
    },
    column: {
        paddingTop: 20,
        paddingBottom: 20,
        borderBottomWidth: 1,
        alignItems: 'center',
        borderColor: 'gray',
        textAlign: 'center',
    },
    lastColumn: {
        flex: 1,
        marginTop: 20,
        marginBottom: 20,
        alignItems: 'center',
        borderColor: 'gray'
    }

})

export default CommunicationsPreferences
