import React, { useState, useEffect } from 'react';
import { StyleSheet, TouchableOpacity, ImageBackground, Image, Text, View, ScrollView } from 'react-native';
import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,
} from 'react-native-responsive-screen';
import Icon from 'react-native-vector-icons/AntDesign'
import {connect} from 'react-redux';
import {getOneProduct} from '../../api/product';
import {Appbar} from 'react-native-paper'
import Icon4 from 'react-native-vector-icons/FontAwesome';

const DetailProduct = (props)=>{

    const [products, setProducts] = useState('')

    useEffect(()=>{
      
      getOneProduct(props.navigation.getParam('id'), props.user.infos.token)
      .then((res)=>{
        setProducts(res.result)
      })
      
  }, [])

return (
    
    <ImageBackground 
      style={ styles.imgBackground } 
      resizeMode='cover' 
      source={require('../../assets/bg3.jpg')}
    >
    <View> 
        {products.length > 0 ?<ScrollView style={styles.ScrollView}>
        <Appbar.Header style={styles.headerContainer}>
          <View style={styles.container}>
            <Image
              style={styles.logo}
              source={require('../../assets/img/logo.png')}
            />
            <Text style={styles.title}
            >
              Bienvenue dans votre Drive
            </Text>            
            <View>
              <TouchableOpacity
                onPress={()=>{
                  props.navigation.navigate('Basket')
                }}
              >
                <Icon4 
                  size={30} 
                  name={'shopping-basket'} 
                  style={styles.icon}
                />
                <Text style={styles.textFromHeader}>
                  Panier
                </Text>
              </TouchableOpacity>
            </View>
          </View>
        </Appbar.Header>
            <View style={styles.box}>
                <View style={styles.col1}>
                    <Text style={styles.text}>{products[0].mark}</Text>
                    {products[0].promotion !== 1 ?
                    <Text style={styles.promo}>-{products[0].promotion}%</Text>
                    : <Text></Text>
                    }
                </View>
                <View style={styles.col2}> 
                    <Image
                        style={styles.img}
                        source={{
                            uri: products[0].photo
                        }}
                    />
                </View>
                <View style={styles.col3}>
                    <Text style={styles.textPrice}>{products[0].price}€</Text>
                    <Icon name="pluscircle" size={30} color="green" />   
                </View>
            </View>
            <View style={styles.col}>
                <Text style={styles.titleBody}>Description :</Text>
                <Text style={styles.text}>{products[0].description}</Text>
            </View>
            <View style={styles.col}>
                <Text style={styles.titleBody}>Ingrédients :</Text>
                <Text style={styles.text}>{products[0].ingredients}</Text>
            </View>
            <View style={styles.col}>
                <Text style={styles.titleBody}>Conservation :</Text>
                <Text style={styles.text}>{products[0].conservation}</Text>
            </View>
            <View style={styles.col}>
                <Text style={styles.titleBody}>Fabriquant :</Text>
                <Text style={styles.text}>{products[0].provider}</Text>
            </View>
            
        </ScrollView>
        :<Text>Une erreur est survenue</Text>}
    
    </View>
    </ImageBackground>

)

}
    const styles = StyleSheet.create({
        ScrollView: {
            width: wp('100%')
        },
        imgBackground: {
            width: '100%',
            height: '100%',
            flex: 1 
        },
        headerContainer: {
            backgroundColor: '#17202a',
                height:50
        },
        container: {
            flex: 1,
            alignItems: 'flex-end',
            flexDirection: 'row',
            justifyContent: 'space-between'
        },
        logo: {
            width:50,
            height:53,
        },
        title: {
            color:'#a3c2fa',
            fontSize: 18
        },
        icon: {
            color:'#a3c2fa',
        },
        textFromHeader: {
            color:"#a3c2fa",
        },
        box: {
            marginTop: 15,
            flexDirection: 'row',
            borderBottomWidth: 1,
            borderColor: 'gray',
            alignItems: 'center'
        },
        col1: {
            width: wp('20%'),
            flexDirection: 'column',
            alignItems: 'center',
        },
        col2: {
            width: wp('60%'),
            flexDirection: 'column',
            alignItems: 'center'
        },
        col3: {
            width: wp('20%'),
            flexDirection: 'column',
            alignItems: 'center'
        },
        col: {
            padding: 10,
            borderBottomWidth: 1,
            borderColor: 'gray',
            alignItems: 'center'
        },
        img: {
            width:wp('40%'),
            height:hp('20%'),
        },
        text: {
            fontSize: 18,
        },
        textPrice: {
            fontSize: 18,
            fontWeight: 'bold',
            marginBottom: 10
        },
        titleBody: {
            fontSize: 18,
            fontWeight: 'bold',
            color: '#191970'
        },
        promo: {
            marginTop: 10,
            padding: 5,
            borderWidth: 1,
            borderRadius: hp('50%'),
            borderColor: 'white',
            backgroundColor: 'red',
            color: 'white',
            fontWeight: 'bold'
        }
    })


mapStateToProps = (store)=>{
    return {
        user: store.user
    }
}
export default connect(mapStateToProps)(DetailProduct);
