import React, {useState, useEffect} from 'react';
import { StyleSheet, TouchableOpacity, Text, TextInput, View, Image, ImageBackground, ScrollView } from 'react-native';
import {
    widthPercentageToDP as wp,
    heightPercentageToDP as hp,
  } from 'react-native-responsive-screen';
import {Appbar} from 'react-native-paper'
import Icon2 from 'react-native-vector-icons/AntDesign';
import {connect} from 'react-redux';
import RNPickerSelect from "react-native-picker-select";
import {userPersonnalInformations} from "../../api/user";


const AccountInformations = (props)=>{

    const [civility, setCivility] = useState(''); 
	const [firstName, setFirstName] = useState(''); 
	const [lastName, setLastName] = useState(''); 
	const [birthday, setBirthday] = useState('');
	const [address, setAddress] = useState('');
	const [zip, setZip] = useState('');
	const [city, setCity] = useState('');
	const [phone, setPhone] = useState('');
    const [data, setData] = useState('');

    userPersonnalInformations(props.navigation.getParam('id'), props.user.infos.token)
    .then((res)=>{
        console.log("RES",res)
        let data = res
        console.log("data",data)
    }) 
  
    return(
        <ImageBackground 
            style={ styles.imgBackground } 
            resizeMode='cover' 
            source={require('../../assets/bg3.jpg')}
        >
        <View>
            <ScrollView style={styles.ScrollView}>
                <Appbar.Header style={styles.headerContainer}>
                <View style={styles.container}>
                    <Image
                        style={styles.logo}
                        source={require('../../assets/img/logo.png')}
                    />
                    <Text style={styles.title}
                    >
                        Bienvenue dans votre Drive
                    </Text>            
                    <View>
                        <TouchableOpacity
                            onPress={()=>{
                                props.navigation.navigate('Logout')
                            }}
                        >
                            <Icon2 
                                size={30} 
                                name={'logout'} 
                                style={styles.icon}
                            />
                            <Text style={styles.logout}
                            >
                                Logout
                            </Text>
                        </TouchableOpacity>
                    </View>
                </View>
            </Appbar.Header>
                <View style={styles.boxHeader}>
                    <Text style={styles.textHeader}>
                        Vos informations de compte :
                    </Text>
                </View>
                <View style={styles.containerBox}>
                    <View style={styles.box}>
                        <View style={styles.column}>
                            <TouchableOpacity
                                onPress={()=>{
                                    props.navigation.navigate('Account')         
                                }}
                            >
                            <Text style={styles.links}>Votre derni√®re commande</Text>
                            </TouchableOpacity>
                        </View>
                    </View>
                    <View style={styles.box}>
                        <View style={styles.column}>
                            <TouchableOpacity
                                onPress={()=>{
                                    props.navigation.navigate('AllOrders')     
                                }}
                            >
                            <Text style={styles.links}>Historique de toutes vos commandes</Text>
                            </TouchableOpacity>
                        </View>
                    </View>
                    <View style={styles.box}>
                        <View style={styles.column}>
                            <TouchableOpacity
                                onPress={()=>{
                                    props.navigation.navigate('CommunicationsPreferences')         
                                }}
                            >
                            <Text style={styles.links}>Vos pr√©f√©rences de communications</Text>
                            </TouchableOpacity>
                        </View>
                    </View>
                    <View style={styles.box2}>
                        <Text style={styles.h1}>A propos de vous :</Text>
                        <View>
                            <View style={styles.rngPicker}>
                                <RNPickerSelect 
                                    placeholder={{
                                        label: 'Civilit√© :', value: 'null'
                                    }}
                                    onValueChange={(value) => setCivility(value)}
                                    items={[
                                        { label: "Monsieur", value: "Monsieur" },
                                        { label: "Madame", value: "Madame" }
                                    ]}	
                                /> 
                            </View>
                            <TextInput style={styles.input}
                                placeholder='Pr√©nom'
                                placeholderTextColor="white"
                                value={firstName}
                                onChangeText={(inputText)=>{
                                    setFirstName(inputText)
                                }}
                            />
                            <TextInput style={styles.input}
                                placeholder="Nom"
                                placeholderTextColor="white" 
                                value={lastName}
                                onChangeText={(inputText)=>{
                                    setLastName(inputText)
                                }}
                            />
                            <TextInput style={styles.input}
                                placeholder="Date de naissance"
                                placeholderTextColor="white" 
                                value={birthday}
                                onChangeText={(inputText)=>{
                                    setBirthday(inputText)
                                }}
                            />
                            <TextInput style={styles.input}
                                placeholder="Adresse"
                                placeholderTextColor="white" 
                                value={address}
                                onChangeText={(inputText)=>{
                                    setAddress(inputText)
                                }}
                            />
                            <TextInput style={styles.input}
                                placeholder="Code postal"
                                placeholderTextColor="white" 
                                value={zip}
                                onChangeText={(inputText)=>{
                                    setZip(inputText)
                                }}
                            />
                            <TextInput style={styles.input}
                                placeholder="Ville"
                                placeholderTextColor="white" 
                                value={city}
                                onChangeText={(inputText)=>{
                                    setCity(inputText)
                                }}
                            />
                            <TextInput style={styles.input}
                                placeholder="T√©l√©phone"
                                placeholderTextColor="white" 
                                value={phone}
                                onChangeText={(inputText)=>{
                                    setPhone(inputText)
                                }}
                            />
                            <TouchableOpacity
                            onPress={(e)=>{
                                
                            }}
                            style={styles.button}
                            >
                            <Text style={styles.buttonText}>Enregistrer les modifications</Text>
                        </TouchableOpacity>
                        </View>  
                    </View>
                </View>
            </ScrollView>
        </View>
        </ImageBackground>
    )
}

const styles = StyleSheet.create({

    imgBackground: {
        width: '100%',
        height: '100%',
        flex: 1 
    },
    ScrollView: {
        width: wp('100%'),
    },
    headerContainer: {
		backgroundColor: '#17202a',
        height:50
	},
	container: {
		flex: 1,
		alignItems: 'flex-end',
        flexDirection: 'row',
        justifyContent: 'space-between'
	},
    logo: {
        width:50,
        height:53,
    },
    title: {
        color:'#a3c2fa',
        fontSize: 18
    },
    h1: {
        fontSize: 25,
        textAlign: 'center',
        paddingTop: hp('1%'),
        paddingBottom: hp('1%')
    },
	icon: {
        color:'#a3c2fa',
    },
    logout: {
        color:"#a3c2fa",
    },
    boxHeader: {
        borderBottomWidth: 3,
        borderColor: '#fff',
        paddingTop: hp('3%'),
        paddingBottom: hp('3%')
    },
    textHeader: {
        color: "#17202a",
        fontWeight: "bold",
        textAlign: 'center',
        fontSize: 25
    },
    containerBox: {
        flexDirection: 'column',
        flex:1
    },
    box: {
        flexDirection: 'row',
        borderColor: '#fff',
        borderBottomWidth: 3,
        justifyContent: 'center',
        backgroundColor: '#17202a',
    },
    box2: {
        flexDirection: 'column'
    },
    column: {
        paddingTop: 10,
        paddingBottom: 10,
        alignItems: 'center',
        borderColor: 'gray',
        textAlign: 'center',
    },
    links: {
        color: '#a3c2fa'
    },
    rngPicker: {
        backgroundColor: '#17202a',
        width: wp('60%'),
        height: 40,
        borderRadius: 50,
        marginBottom: 15,
        marginLeft: wp('20%'),
        paddingLeft: wp('5%'),
        color: "white"
    },
      input: {
        backgroundColor: '#17202a',
        width: wp('60%'),
        height: 40,
        borderRadius: 50,
        marginBottom: 15,
        marginLeft: wp('20%'),
        paddingLeft: wp('5%'),
        color: 'white'
      },
      button: {
        backgroundColor: "#219653",
        borderRadius: 50,
        width: wp('50%'),
        height: 60,
        alignItems: "center",
        justifyContent: "center",
        marginTop: wp('5%'),
        marginBottom: wp('10%'),
        marginLeft: wp('24%'),
        
      },
      buttonText: {
        fontSize: 18,
        color: "white",
        textAlign: 'center'
      }

})
  
mapStateToProps = (store)=>{
    return {
        user: store.user
    }
}
